import { createContext, useReducer } from "react";
import { AuthReducer } from "./Auth-reducer";

const INITIAL_STATE = {
  user: {
    _id: "62668f1c09f0845ec9d7110a",
    username: "elias",
    email: "elias",
    "password": "$2b$10$yi08kgRqHu.nBlEyD6fDmef7T/xlw/7TrMfw0KBgpas4pFlGPEnTS",
    "profilePicture": "",
    "coverPicture": "",
    "followers": [],
    "followings": [
        "626553f8f33f1e1e11f45a41"
    ],
    "isAdmin": false,
    "createdAt": "2022-04-25T12:07:56.359Z",
    "updatedAt": "2022-04-25T12:07:56.359Z",
    "__v": 0
},
  isFetching: false,
  error: false,
};

export const AuthContext = createContext(INITIAL_STATE);

export const AuthContextProvider = ({ children }) => {
  const [state, dispatch] = useReducer(AuthReducer, INITIAL_STATE);

  return (
    <AuthContext.Provider
      value={{
        user: state.user,
        isFetching: state.isFetching,
        error: state.error,
        dispatch,
      }}
    >
      {children}
    </AuthContext.Provider>
  );
};
