import React, { useContext } from "react";
import "./Profile.css";
import SideBar from "../../Components/SideBar/SideBar";
import TopBar from "../../Components/TopBar/TopBar";
import Feed from "../../Components/Feed/Feed";
import {useState,useEffect} from 'react';
import axios from 'axios';
import Rightbar from "../../Components/RightBar/RightBar";
import {AuthContext} from '../../Components/Context/Auth-context';

function Profile() {

  const {user} = useContext(AuthContext)

  const [currentUser,setUser] = useState({});

  useEffect(()=>{
    const fetchUser = async()=>{
      const res = await axios.get(`http://localhost:8800/users/username=Elias Admaso`);
      setUser(res.data)

    }

  fetchUser()

  },[])


  return (
    <>
      <TopBar />
      <div className="profile">
        <SideBar />
        <div className="profileRight">
          <div className="profileRightTop">
            <div className="profileCover">
              <img
                src="https://wallpaperaccess.com/full/14736.jpg"
                className="profileCoverImg"
              />
              <img
                src="https://images.unsplash.com/photo-1546456073-ea246a7bd25f?ixlib=rb-1.2.1&ixid=MnwxMjA3fDB8MHxzZWFyY2h8MTB8fGJsYWNrJTIwbWFufGVufDB8fDB8fA%3D%3D&w=1000&q=80"
                className="profileUserImg"
              />
            </div>
            <div className="profileInfo">
                <h4 className="profileInfoName">{user.username}</h4>
            </div>
          </div>
          <div className="profileRightBottom">
            <Feed username={user.username}/>
            <Rightbar/>

          </div>
        </div>
      </div>
    </>
  );
}

export default Profile;
