import axios from "axios";

const updateUser = async (userId,username,from,city,relationship,profilePicture,coverPicture) => {
  try {
    await axios.put("http://localhost:8800/users/update", {
      userId,
      username,
      from,
      city,
      relationship,
      profilePicture,
      coverPicture,
    });
    dispatch({
        type: "UPDATE_USER",
        payload:
          (
        username.current.value,
          from.current.value,
          city.current.value,
          relationship.current.value,
          profilePicture.current.value,
          coverPicture.current.value),
      });

  } catch (error) {
      console.log(error);
  }
};

export {updateUser};
