import axios from "axios";
import { useContext } from "react";

const {user} = useContext(authc)


const updateUser = async (
  userId,
  username,
  from,
  city,
  relationship,
  profilePicture,
  coverPicture,
  dispatch
) => {
  try {
    await axios.put("http://localhost:8800/users/update", {
      userId,
      username,
      from,
      city,
      relationship,
      profilePicture,
      coverPicture,
    });
    const res = await axios.get(`http://localhost:8800/users/${userId}`);
    dispatch({
      type: "UPDATE_USER",
      payload: res.data,
    });
  } catch (error) {
    console.log(error);
  }
};

export { updateUser };
