import React, { useContext } from "react";
import "./Edit.css";
import { useRef } from "react";
import { updateUser } from "../../Components/Service/user-service";
import { AuthContext } from "../../Components/Context/Auth-context";

function Edit() {
  const { user } = useContext(AuthContext);

  const handleClick = async () => {
    const updateDetails = {
      username: username.current.value,
      from: from.current.value,
      city: city.current.value,
      relationship: relationship.current.value,
      profilePicture: profilePicture.current.value,
      coverPicture: coverPicture.current.value,
    };

    try {
      await updateUser(user._id, updateDetails);
    } catch (error) {
      console.log(error);
    }
  };

  return (
    <div className="editContainer">
      <div className="container">
        <form id="form" className="form">
          <h2>Update Your Details</h2>
          <div className="form-control">
            <label for="username">Username</label>
            <input type="text" placeholder="Enter username" ref={username} />
          </div>
          <div className="form-control">
            <label for="email">From</label>
            <input type="text" placeholder="Enter email" ref={from} />
          </div>
          <div className="form-control">
            <label for="password">City</label>
            <input type="password" placeholder="Enter password" ref={city} />
          </div>
          <div className="form-control">
            <label for="password">Relationship</label>
            <input
              type="password"
              placeholder="Enter password"
              ref={relationship}
            />
          </div>
          <div className="form-control">
            <label for="password">Profile Picture</label>
            <input
              type="password"
              placeholder="Enter password"
              ref={profilePicture}
            />
          </div>
          <div className="form-control">
            <label for="password">Cover Picture</label>
            <input
              type="password"
              placeholder="Enter password"
              ref={coverPicture}
            />
          </div>
          <button className="auth-btn" onClick={handleClick}>
            Update
          </button>
        </form>
      </div>
    </div>
  );
}

export default Edit;
