import { createContext, useReducer } from "react";
import { AuthReducer } from "./Auth-reducer";

const INITIAL_STATE = {
  user: {
    _id: "626553f8f33f1e1e11f45a41",
    username: "Elias Admaso",
    email: "elias1@gmail.com",
    password: "$2b$10$wFhHu8ylYr1A6SbeWiyMf.p/yRJiZGmSNP7b9Q962wNspI4.jXcD6",
    profilePicture: "https://media-exp1.licdn.com/dms/image/D4E03AQGwu9iwXSTnnQ/profile-displayphoto-shrink_800_800/0/1637073753594?e=1656547200&v=beta&t=1jyyuQin3-vsOwVVlL82M0M0_cC5IxuA3uo26FeYDyk",
    coverPicture: "",
    followers: [],
    followings: [],
    isAdmin: false,
    __v: 0
},
  isFetching: false,
  error: false,
};

export const AuthContext = createContext(INITIAL_STATE);

export const AuthContextProvider = ( children ) => {
  const [state, dispatch] = useReducer(AuthReducer, INITIAL_STATE);

  return (
    <AuthContext.Provider
      value={{
        user: state.user,
        isFetching: state.isFetching,
        error: state.error,
        dispatch,
      }}
    >
      {children}
    </AuthContext.Provider>
  );
};
