export const AuthReducer = (state, action) => {
  switch (action.type) {
    case "LOGIN_START":
      return {
        user: null,
        isFetching: true,
        error: false,
      };

    case "LOGIN_SUCCESS":
      return {
        user: action.payload,
        isFetching: false,
        error: false,
      };

    case "LOGIN_FAILURE":
      return {
        user: null,
        isFetching: false,
        error: action.payload,
      };
      case "LOGIN_FAILURE":
        return {
          user: action.payload,
          isFetching: false,
          error: false,
        };
      case "FOLLOW":
        return {
          ...state,
          user:{
            ...state.user,
            followings:[...state.user.followings,action.payload]
          },
        };
        case "UNFOLLOW":
          return {
            ...state,
            user:{
              ...state.user,
              followings:state.user.followings.filter(
                (following)=>following !== action.payload
              ),
            },
          };
          case "UPDATE_USER":
            return {

              user:{
                ...state.user,
                username:action.payload.username,
                from:action.payload.from,
                city:action.payload.city,
                relationship:action.payload.relationship,
                profilePicture:action.payload.profilePicture,
                coverPicture:action.payload.coverPicture

              }
            };
    default:
      return state;
  }
};
