import React, { useContext } from "react";
import "./Edit.css";
import { useRef } from "react";
import { updateUser } from "../../Components/Service/user-service";
import { AuthContext } from "../../Components/Context/Auth-context";
import TopBar from "../../Components/TopBar/TopBar";

function Edit() {
  const { user,dispatch } = useContext(AuthContext);

  const username = useRef();
  const from = useRef();
  const city = useRef();
  const relationship = useRef();
  const profilePicture = useRef();
  const coverPicture = useRef();

  const handleClick = async (e) => {
    e.preventDefault();
    try {
      await updateUser(
        user._id,
        username.current.value,
        from.current.value,
        city.current.value,
        relationship.current.value,
        profilePicture.current.value,
        coverPicture.current.value
      );
      dispatch({type:"UPDATE"})

    } catch (error) {
      console.log(error);
    }
  };

  return (
      <>
      <TopBar/>
    <div className="editContainer">
      <div className="container">
        <form id="form" className="form">
          <h2>Update Your Details</h2>
          <div className="form-control">
            <label for="username">Username</label>
            <input type="text" placeholder="Enter username" ref={username} />
          </div>
          <div className="form-control">
            <label for="from">From</label>
            <input type="text" placeholder="Enter email" ref={from} />
          </div>
          <div className="form-control">
            <label for="city">City</label>
            <input type="text" placeholder="Enter password" ref={city} />
          </div>
          <div className="form-control">
            <label for="relationship">Relationship</label>
            <input
              type="text"
              placeholder="Enter password"
              ref={relationship}
            />
          </div>
          <div className="form-control">
            <label for="picture">Profile Picture</label>
            <input
              type="text"
              placeholder="Enter password"
              ref={profilePicture}
            />
          </div>
          <div className="form-control">
            <label for="cover">Cover Picture</label>
            <input
              type="text"
              placeholder="Enter password"
              ref={coverPicture}
            />
          </div>
          <button className="auth-btn" onClick={handleClick}>
            Update
          </button>
        </form>
      </div>
    </div>
    </>
  );
}

export default Edit;
