import { createContext, useReducer } from "react";
import { AuthReducer } from "./Auth-reducer";

const INITIAL_STATE = {
  user:  {
    _id: "626e8c029c5b2f5b0f9f6761",
    username: "Daniel Admaso",
    email: "d",
    password: "$2b$10$Y2cciUfWayoqW42e58yk5Osvn17TQ4NvIX.Ji5c/XimV7rSd5CX2O",
    "profilePicture": "",
    "coverPicture": "",
    "followers": [
        "626e8dc09c5b2f5b0f9f6787"
    ],
    "followings": [],
    "isAdmin": false,
    "createdAt": "2022-05-01T13:32:50.792Z",
    "updatedAt": "2022-05-01T13:53:07.390Z",
    "__v": 0
},
  isFetching: false,
  error: false,
};

export const AuthContext = createContext(INITIAL_STATE);

export const AuthContextProvider = ({ children }) => {
  const [state, dispatch] = useReducer(AuthReducer, INITIAL_STATE);

  return (
    <AuthContext.Provider
      value={{
        user: state.user,
        isFetching: state.isFetching,
        error: state.error,
        dispatch,
      }}
    >
      {children}
    </AuthContext.Provider>
  );
};
