import React, { useContext, useRef } from 'react';
import './Auth.css';
import {LoginUser} from '../../Components/Service/auth-service';
import {AuthContext} from '../../Components/Context/Auth-context';

function Login() {

    const {user,isFetching,error,dispatch} = useContext(AuthContext);

    const email = useRef();
    const password = useRef();

    const handleClick = (e)=>{
        e.preventDefault();
        LoginUser({email:email.current.value,password:password.current.value})
    }

    return (
        <div className="container">
        <form  id="form" className="form">
            <h2>Login</h2>
            <div className="form-control">
                <label for="email">Email</label>
                <input ref={email} type="text" id="email" placeholder="Enter email"/>
            </div>
            <div className="form-control">
                <label for="password">Password</label>
                <input ref={password} type="password" id="password" placeholder="Enter password"/>
            </div>
            <button type="submit" onClick={handleClick}>Submit</button>
        </form>
    </div>
    )
}

export default Login
